import React, { useEffect, useState, useContext } from "react";
import { StoreContext } from "../context/StoreContext";
import { LanguageContext } from "../context/LanguageContext";
import { toast } from "react-toastify";
import { ShoppingBag } from "lucide-react";

export default function ProductsPage({ category }) {
  const [products, setProducts] = useState([]);
  const { addToCart } = useContext(StoreContext);
  const { t } = useContext(LanguageContext);

  useEffect(() => {
    async function fetchProducts() {
      try {
        const res = await fetch("http://localhost:5000/products?limit=100");
        const data = await res.json();

        let filtered = [];

        if (category === "beauty") {
          filtered = data.products.filter(p =>
            ["beauty", "fragrances"].includes(p.category.toLowerCase())
          );
        } else if (category === "groceries") {
          filtered = data.products.filter(p =>
            ["groceries"].includes(p.category.toLowerCase())
          );
        } else if (category === "furniture") {
          filtered = data.products.filter(p =>
            ["furniture"].includes(p.category.toLowerCase())
          );
        }

        setProducts(filtered);
      } catch (err) {
        console.error(err);
        // Fallback for demo purposes if API fails (e.g. CORS)
        setProducts([
          {
            id: 1,
            title: "Demo Product 1",
            price: 19.99,
            category: category,
            thumbnail: "https://via.placeholder.com/150"
          },
          {
            id: 2,
            title: "Demo Product 2",
            price: 29.99,
            category: category,
            thumbnail: "https://via.placeholder.com/150"
          }
        ]);
        toast.error("API Error: Using demo data");
      }
    }

    fetchProducts();
  }, [category]);

  const handleAddToCart = (product) => {
    addToCart(product);
    toast.success(t('notifications.addedToCart'), {
      icon: <ShoppingBag size={20} />
    });
  };

  return (
    <div className="container my-4">
      <h2 className="mb-4 text-capitalize">
        {t(`products.${category}`)}
      </h2>
      <div className="row">
        {products.length === 0 && <p className="text-muted">{t('products.notFound')}</p>}
        {products.map(p => (
          <div key={p.id} className="col-md-3 mb-4">
            <div className="card h-100 shadow-sm product-card">
              <img src={p.thumbnail} alt={p.title} className="card-img-top" />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{p.title}</h5>
                <p className="card-text text-muted">{t('products.price')}{p.price}</p>
                <button
                  className="btn btn-custom mt-auto"
                  onClick={() => handleAddToCart(p)}
                >
                  {t('products.addToCart')}
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
