import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { StoreContext } from "../context/StoreContext";
import { LanguageContext } from "../context/LanguageContext";
import { ShoppingCart, User, Menu, Globe } from "lucide-react";

export default function Navbar() {
  const { cart, user } = useContext(StoreContext);
  const { t, toggleLanguage, language } = useContext(LanguageContext);

  return (
    <nav className="navbar navbar-expand-lg navbar-dark navbar-custom shadow">
      <div className="container">
        {/* Brand */}
        <Link className="navbar-brand fw-bold" to="/">
          {t('navbar.brand')}
        </Link>

        {/* Toggler (hamburger button) */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <Menu className="text-white" />
        </button>

        {/* Collapsible content */}
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/beauty">
                {t('navbar.beauty')}
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/groceries">
                {t('navbar.groceries')}
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/furniture">
                {t('navbar.furniture')}
              </Link>
            </li>
          </ul>
          <ul className="navbar-nav ms-auto align-items-center">
            {/* Language Toggle */}
            <li className="nav-item">
              <button
                className="btn btn-link nav-link d-flex align-items-center"
                onClick={toggleLanguage}
                style={{ textDecoration: 'none' }}
              >
                <Globe size={20} className="me-1" />
                {language.toUpperCase()}
              </button>
            </li>

            {user && (
              <li className="nav-item nav-link d-flex align-items-center">
                <User size={20} className="me-1" />
                {t('navbar.greeting')}, {user.email}
              </li>
            )}

            <li className="nav-item">
              <Link className="nav-link d-flex align-items-center" to="/cart">
                <ShoppingCart size={20} className="me-1" />
                {t('navbar.cart')} ({cart.length})
              </Link>
            </li>

            {!user && (
              <li className="nav-item">
                <Link className="nav-link d-flex align-items-center" to="/login">
                  <User size={20} className="me-1" />
                  {t('navbar.login')}
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}
