import { useEffect, useState } from "react";
import axios from "axios";

export default function ProductsPage({ category }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then(res => {
        let filtered = res.data;

        // Simulación de categorías
        if (category === "bebidas") {
          filtered = filtered.filter(p => p.category.includes("drink"));
        } else if (category === "alimentos") {
          filtered = filtered.filter(p => p.category.includes("food"));
        } else if (category === "hogar") {
          filtered = filtered.filter(p => p.category.includes("home"));
        } else if (category === "otros") {
          filtered = filtered.filter(p => !p.category.includes("food") && !p.category.includes("drink") && !p.category.includes("home"));
        }

        setProducts(filtered);
      });
  }, [category]);

  return (
    <div className="container my-4">
      <h2 className="mb-4 text-capitalize">{category}</h2>
      <div className="row">
        {products.map(p => (
          <div key={p.id} className="col-md-3 mb-4">
            <div className="card h-100 shadow-sm">
              <img src={p.thumbnail} alt={p.title} className="card-img-top" />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{p.title}</h5>
                <p className="card-text text-muted">${p.price}</p>
                <button className="btn btn-success mt-auto">Agregar</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
